import React, { useEffect } from "react";

function GraphVisualization({ adjMatrix, vertices, visited, queue, currentStep }) {
  useEffect(() => {
    // Оновлення графа для поточного кроку (якщо потрібно)
  }, [currentStep, adjMatrix, visited, queue]);

  return (
    <div className="graph-visualization">
      <h3>Graph Visualization (Adjacency Matrix)</h3>
      <table className="adj-matrix">
        <thead>
          <tr>
            <th></th>
            {vertices.map((vertex, idx) => (
              <th key={idx}>V{vertex}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {adjMatrix.map((row, rowIndex) => (
            <tr key={rowIndex}>
              <td>V{vertices[rowIndex]}</td>
              {row.map((cell, cellIndex) => (
                <td
                  key={cellIndex}
                  className={`matrix-cell ${
                    visited.includes(vertices[cellIndex]) ? "visited" : 
                    queue.includes(vertices[cellIndex]) ? "in-queue" : ""
                  }`}
                >
                  {cell}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default GraphVisualization;
