import React, { useState } from "react";
import GraphInput from "./components/GraphInput";
import GraphVisualization from "./components/GraphVisualization";
import Algorithms from "./components/Algorithms";
import "./styles/App.css";

function App() {
  const [matrix, setMatrix] = useState([]); // Матриця суміжності
  const [vertices, setVertices] = useState([]); // Вершини графа
  const [isStartPage, setIsStartPage] = useState(true); // Стан для початкової сторінки
  const [mode, setMode] = useState(""); // Вибраний режим

  const handleMatrixSubmit = (newMatrix) => {
    setMatrix(newMatrix);
    setVertices(generateVertices(newMatrix.length));
  };

  const generateVertices = (count) => {
    const angleStep = (2 * Math.PI) / count;
    const radius = 150;

    return Array.from({ length: count }, (_, i) => ({
      x: 300 + radius * Math.cos(i * angleStep),
      y: 300 + radius * Math.sin(i * angleStep),
    }));
  };

  // Обробка вибору режиму
  const handleStart = (selectedMode) => {
    setMode(selectedMode);
    setIsStartPage(false);
  };

  return (
    <div className="App">
      {isStartPage ? (
        <div className="start-page">
          <header>
            <h1>Website Wireframe</h1>
          </header>
          <div className="main">
            <div className="card">
              <h2>Your Graph Algorithm</h2>
              <button className="start-button" onClick={() => handleStart("graph")}>
                Start
              </button>
            </div>
            <div className="card">
              <h2>Your Data Structure</h2>
              <button className="start-button" onClick={() => handleStart("data-structure")}>
                Start
              </button>
            </div>
          </div>
          <footer>
            <p>Internet of Things</p>
            <p>2025</p>
          </footer>
        </div>
      ) : (
        <div>
          <header>
            <h1>{mode === "graph" ? "Graph Algorithms Visualization" : "Data Structure Visualization"}</h1>
          </header>

          {mode === "graph" && (
            <>
              <GraphInput onMatrixSubmit={handleMatrixSubmit} />
              <GraphVisualization matrix={matrix} vertices={vertices} />
              <Algorithms matrix={matrix} />
            </>
          )}

          {mode === "data-structure" && <p>Data Structure functionality will be here</p>}
        </div>
      )}
    </div>
  );
}

export default App;
