import React, { useEffect, useRef } from "react";
import * as d3 from "d3";

const BSTVisualizer = ({ tree }) => {
  const svgRef = useRef();

  useEffect(() => {
    if (!tree) return;

    const svg = d3.select(svgRef.current);
    const width = 1000;
    const height = 500;

    // Очистка SVG перед повторним малюванням
    svg.selectAll("*").remove();

    svg.attr("width", width)
      .attr("height", height)
      .style("background", "#f9f9f9")
      .style("border", "1px solid #ccc");

    const drawTree = (node, x, y, depth) => {
      if (!node) return;

      const offsetX = 300 / (depth + 1); // Горизонтальне зміщення
      const offsetY = 80; // Вертикальне зміщення

      // Малювання лінії до лівої гілки
      if (node.left) {
        svg.append("line")
          .attr("x1", x)
          .attr("y1", y)
          .attr("x2", x - offsetX)
          .attr("y2", y + offsetY)
          .attr("stroke", "green")
          .attr("stroke-width", 2);

        drawTree(node.left, x - offsetX, y + offsetY, depth + 1);
      }

      // Малювання лінії до правої гілки
      if (node.right) {
        svg.append("line")
          .attr("x1", x)
          .attr("y1", y)
          .attr("x2", x + offsetX)
          .attr("y2", y + offsetY)
          .attr("stroke", "green")
          .attr("stroke-width", 2);

        drawTree(node.right, x + offsetX, y + offsetY, depth + 1);
      }

      // Малювання вузла
      svg.append("circle")
        .attr("cx", x)
        .attr("cy", y)
        .attr("r", 20)
        .attr("fill", "#fff")
        .attr("stroke", "#000")
        .attr("stroke-width", 2);

      // Текст у вузлі
      svg.append("text")
        .attr("x", x)
        .attr("y", y + 5)
        .attr("text-anchor", "middle")
        .attr("font-size", "12px")
        .text(node.value);
    };

    // Малюємо дерево з кореня
    drawTree(tree, width / 2, 50, 1);
  }, [tree]); // Виклик useEffect при зміні дерева

  return (
    <div>
      <svg ref={svgRef}></svg>
      <div style={{ marginTop: "20px" }}>
        <button onClick={() => alert("Pause functionality not implemented yet")}>Pause</button>
        <button onClick={() => alert("Step Back functionality not implemented yet")}>Step Back</button>
        <button onClick={() => alert("Step Forward functionality not implemented yet")}>Step Forward</button>
      </div>
    </div>
  );
};

export default BSTVisualizer;
