import React, { useEffect } from "react";

function GraphVisualization({ vertices, edges, queue, visited, currentStep }) {
  useEffect(() => {
    // Оновлення графа для поточного кроку
    // Тут можна додавати візуалізацію кожної вершини (сірі, чорні, черга)
  }, [currentStep, vertices, edges, queue, visited]);

  return (
    <div className="graph-visualization">
      <h3>Graph Visualization</h3>
      {/* Ваша логіка для візуалізації графа */}
      <div className="graph">
        {/* Приклад відображення графа */}
        {vertices.map((vertex, index) => (
          <div
            key={index}
            className={`vertex ${
              visited.includes(vertex) ? "visited" : queue.includes(vertex) ? "in-queue" : "unvisited"
            }`}
          >
            {vertex}
          </div>
        ))}
      </div>
      <div className="queue">
        <h4>Queue:</h4>
        {queue.join(", ")}
      </div>
    </div>
  );
}

export default GraphVisualization;
