import React, { useLayoutEffect, useRef } from "react";
import * as d3 from "d3";
import "../styles/BSTVisualizer.css";

const BSTVisualizer = ({ tree, searchValue }) => {
  const svgRef = useRef();

  useLayoutEffect(() => {
    if (!tree) return;  // Перевірка чи дерево існує

    const svg = d3.select(svgRef.current);
    const width = 800;
    const height = 400;

    svg.selectAll("*").remove();

    svg
      .attr("width", width)
      .attr("height", height)
      .style("background", "#f9f9f9")
      .style("border", "1px solid #ccc");

    const drawTree = (node, x, y, depth) => {
      if (!node) return;

      const offsetX = 300 / (depth + 1);
      const offsetY = 80;

      if (node.left) {
        svg.append("line")
          .attr("x1", x)
          .attr("y1", y)
          .attr("x2", x - offsetX)
          .attr("y2", y + offsetY)
          .attr("stroke", "#00796b")
          .attr("stroke-width", 2);

        drawTree(node.left, x - offsetX, y + offsetY, depth + 1);
      }

      if (node.right) {
        svg.append("line")
          .attr("x1", x)
          .attr("y1", y)
          .attr("x2", x + offsetX)
          .attr("y2", y + offsetY)
          .attr("stroke", "#00796b")
          .attr("stroke-width", 2);

        drawTree(node.right, x + offsetX, y + offsetY, depth + 1);
      }

      svg.append("circle")
        .attr("cx", x)
        .attr("cy", y)
        .attr("r", 20)
        .attr("fill", node.value === searchValue ? "#ffeb3b" : "#ffffff")
        .attr("stroke", "#000")
        .attr("stroke-width", 2);

      svg.append("text")
        .attr("x", x)
        .attr("y", y + 5)
        .attr("text-anchor", "middle")
        .attr("font-size", "12px")
        .attr("font-weight", "bold")
        .text(node.value);
    };

    const visualizeSearch = (node, x, y, depth) => {
      if (!node) return;

      const offsetX = 300 / (depth + 1);
      const offsetY = 80;

      svg.append("circle")
        .attr("cx", x)
        .attr("cy", y)
        .attr("r", 20)
        .attr("fill", "#ccc")
        .attr("stroke", "#000")
        .attr("stroke-width", 2);

      if (node.value === searchValue) return;

      setTimeout(() => {
        if (searchValue < node.value) {
          visualizeSearch(node.left, x - offsetX, y + offsetY, depth + 1);
        } else {
          visualizeSearch(node.right, x + offsetX, y + offsetY, depth + 1);
        }
      }, 1000);
    };

    drawTree(tree, width / 2, 50, 1);

    if (searchValue !== null && !isNaN(searchValue)) {
      visualizeSearch(tree, width / 2, 50, 1);
    }

  }, [tree, searchValue]);

  return <svg ref={svgRef}></svg>;
};

export default BSTVisualizer;
