import React, { useState } from "react";
import BSTVisualizer from "../components/BSTVisualizer";
import "../styles/BinarySearchTree.css";

// Клас TreeNode для представлення вузлів дерева
class TreeNode {
  constructor(value) {
    this.value = value;
    this.left = null;
    this.right = null;
  }
}

// Клас BinarySearchTree з методами для роботи з деревом
class BinarySearchTrees {
  constructor() {
    this.root = null;
  }

  // Метод для вставки вузла
  insert(value) {
    const newNode = new TreeNode(value);
    if (!this.root) {
      this.root = newNode;
      return;
    }
    let current = this.root;
    while (true) {
      if (value < current.value) {
        if (!current.left) {
          current.left = newNode;
          return;
        }
        current = current.left;
      } else {
        if (!current.right) {
          current.right = newNode;
          return;
        }
        current = current.right;
      }
    }
  }

  // Метод для пошуку вузла
  find(value) {
    let current = this.root;
    while (current) {
      if (value === current.value) return true;
      current = value < current.value ? current.left : current.right;
    }
    return false;
  }

  // Метод для видалення вузла
  delete(value) {
    const deleteNode = (node, value) => {
      if (!node) return null;
      if (value < node.value) {
        node.left = deleteNode(node.left, value);
        return node;
      } else if (value > node.value) {
        node.right = deleteNode(node.right, value);
        return node;
      } else {
        // Видаляється вузол з одним або двома дочірніми елементами
        if (!node.left) return node.right;
        if (!node.right) return node.left;

        // Видаляється вузол з двома дочірніми елементами
        let successor = node.right;
        while (successor.left) {
          successor = successor.left;
        }
        node.value = successor.value;
        node.right = deleteNode(node.right, successor.value);
        return node;
      }
    };
    this.root = deleteNode(this.root, value);
  }

  // Метод для отримання структури дерева
  getTree() {
    return this.root;
  }
}

// Функціональний компонент для роботи з BinarySearchTree
const BinarySearchTreeComponent = () => {
  const [tree, setTree] = useState(new BinarySearchTrees());
  const [inputValue, setInputValue] = useState("");

  // Обробка вставки
  const handleInsert = () => {
    if (!isNaN(inputValue) && inputValue !== "") {
      tree.insert(parseInt(inputValue));
      setTree(new BinarySearchTrees(tree)); // Оновлення дерева
      setInputValue("");
    }
  };

  // Обробка видалення
  const handleDelete = () => {
    if (!isNaN(inputValue) && inputValue !== "") {
      tree.delete(parseInt(inputValue));
      setTree(new BinarySearchTrees(tree)); // Оновлення дерева
      setInputValue("");
    }
  };

  // Обробка пошуку
  const handleFind = () => {
    if (!isNaN(inputValue) && inputValue !== "") {
      const found = tree.find(parseInt(inputValue));
      alert(found ? "Node found" : "Node not found");
    }
  };

  return (
    <div>
      <h1>Binary Search Tree</h1>
      <div>
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="Enter a number"
        />
        <button onClick={handleInsert}>Insert</button>
        <button onClick={handleDelete}>Delete</button>
        <button onClick={handleFind}>Find</button>
      </div>
      <BSTVisualizer tree={tree.getTree()} />
    </div>
  );
};

export { BinarySearchTrees };
export default BinarySearchTreeComponent;
